                    itemList.map(item => (
                            <PortfolioItem 
                                title={item.title} 
                                id={item.id} 
                                active={item.id === selected} 
                                setSelected={setSelected} 
                            />)
                        )
                }
            </ul>
            <div className="container">
                <div className="item">
                    <img src={math_eqn} />
